import React, { useState } from 'react';
import './CardsLoader.css';
import data from '../../data.json';
import Spinner from '../Spinner/Spinner';

const imagesArr:Array<string> = [
    'https://images.pexels.com/photos/45201/kitty-cat-kitten-pet-45201.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,']
  

interface CardData {
  type: string;
  title: string;
  position: number;
  image?:string;
}

const CardsLoader: React.FC = () => {
  const mergeDataNImg = (data:CardData[],images: string[]) => 
    data.map((d,i)=> {return {...d,image:images[i]}});
  
  const [cards, setCards] = useState<CardData[]>(mergeDataNImg(data,imagesArr));
  const [selectedImage, setSelectedImage] = useState<string | undefined | null>(null);
  const [loading, setLoading] = useState<boolean>(true);

  setTimeout(() => {
    if (loading) {
      setLoading(false);
    }
  }, 1500);

  const handleCardClick = (imageUrl: string | undefined) => {
    if(imageUrl) return
    setSelectedImage(imageUrl);
  };

  const handleDragStart = (e: React.DragEvent<HTMLDivElement>, index: number) => {
    e.dataTransfer.setData('text/plain', index.toString());
  };

  const handleDrop = (e: React.DragEvent<HTMLDivElement>, index: number) => {
    const draggedIndex = Number(e.dataTransfer.getData('text/plain'));
    if (draggedIndex !== index) {
      const updatedCards = [...cards];
      const [movedCard] = updatedCards.splice(draggedIndex, 1);
      updatedCards.splice(index, 0, movedCard);
      setCards(updatedCards);
    }
    e.preventDefault();
  };

  const handleDragOver = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); // Necessary to allow dropping
  };

  return (
    <>
      <div className="cards">
        {cards.map((card, index) => (
          <div
            className="card"
            key={card.position}
            draggable
            onDragStart={(e) => handleDragStart(e, index)}
            onDrop={(e) => handleDrop(e, index)}
            onDragOver={handleDragOver}
            onClick={() => handleCardClick(card?.image)}
          >
            <p>{card.title}</p>
            <div className='image-container'>
            {loading && <Spinner />}
            <img
              src={card?.image}
              alt={card.title}
              style={{ visibility: loading ? 'hidden' : 'visible' }} 
            />
            </div>
          </div>
        ))}
      </div>
      {selectedImage && (
        <div className="overlay" onClick={() => setSelectedImage(null)}>
          <img src={selectedImage} alt="Selected" />
        </div>
      )}
    </>
  );
};

export default CardsLoader;